
// Shop menu visibility controller
$(document).ready(function() {
  $('.product-card__shop-popup>button').click(function() {
    if ($(this).next('.product-card__shop-list').css("display") == "none") {
      $('.product-card__shop-list').hide('show');
      $(this).next('.product-card__shop-list').toggle('show');
    } else $('.product-card__shop-list').hide('show');
    return false;
  });
});
// Item size menu visibility controller
$(document).ready(function() {
  $('.product-card__size-popup>button').click(function() {
    if ($(this).next('.product-card__size-list').css("display") == "none") {
      $('.product-card__size-list').hide('show');
      $(this).next('.product-card__size-list').toggle('show');
    } else $('.product-card__size-list').hide('show');
    return false;
  });
});


// Switch productc-card image
$(".product-card__shoping-cart-image").on("click", function(event) {
  var shopingCart = "../assets/img/product-card/shoping-cart.svg";
  var activeCart = "./assets/img/product-card/shoping-cart--active.svg";

  $(this).attr(
     "src", 
     $(this).attr("src") === shopingCart ? activeCart : shopingCart
  );
  event.preventDefault();
  return false;
});


// Hover mode for menu shoping-cart
$(".menu__shoping-cart-image").on({
  mouseenter: function () {
    $(this).attr("src", "../assets/img/menu/scales--hover.svg");
  },
  mouseleave: function () {
    $(this).attr("src", "../assets/img/menu/scales.svg");
  }
});

// Hover mode for menu search
$(".menu__serach-image").on({
  mouseenter: function () {
    $(this).attr("src", "../assets/img/menu/serach--hover.svg");
  },
  mouseleave: function () {
    $(this).attr("src", "../assets/img/menu/serach.svg");
  }
});


